# --// Initialisation

from math import *

# --// Functions

def Add(Num1, Num2):
    return Num1 + Num2

def Subtract(Num1, Num2):
    return Num1 - Num2

def Multiply(Num1, Num2):
    return Num1 * Num2

def Devide(Num1, Num2):
    return Num1 / Num2

def Absolute(Num):
    return abs(Num)

def Power(Num1, Num2):
    return pow(Num1, Num2)  # --/ Number, Exponent value

def Round(Num):
    return round(Num)

def SquareRoot(Num):
    return sqrt(Num)